package com.maxcheung.puzzle.math;

import static org.junit.Assert.assertEquals;

import java.math.BigInteger;

import org.junit.Ignore;
import org.junit.Test;

public class SequenceCalcMiscellaneousTest {
    SequenceCalc sequenceCalc = new SequenceCalc();

    private static final int ONE_MIL = 1_000_000;
    private static final int TEN_MIL = 10_000_000;

    @Test
    /*
     * Order of magnitude: n + c
     *
     * Approx execution time 3secs
     */
    public void shouldCalculatePrecedingSum1Mil() {
        assertEquals(
                new BigInteger(
                        ""),
                sequenceCalc.sumNthElement(ONE_MIL, 200));
    }

    @Test
    /*
     * Order of magnitude: n + c
     *
     * Approx execution time 3secs
     */
    public void shouldCalculatePrecedingSumSegment() {
        BigInteger total = BigInteger.ZERO;
        BigInteger currElement = BigInteger.ZERO;
        final int nElements = 100;
        final int nPrecedingElements = 10;
        int segments = 4;
        int chunksize = nElements / segments;
        int start;
        int end;

        start = 1;
        for (int i = 1; i < segments; i++) {
            end = Math.max(i * chunksize, 100);
            currElement = sequenceCalc.sumNthElementSegment(nPrecedingElements, start, end);
            start = end;
            total = total.add(currElement);
        }

        assertEquals(new BigInteger("513946235090696089113"), total);
        assertEquals(new BigInteger("513946235090696089113"),
                sequenceCalc.sumNthElement(nElements, nPrecedingElements));

    }

    @Test
    @Ignore
    /*
     * Order of magnitude: n + c
     *
     * Approx execution time 34 secs
     */
    public void shouldCalculatePrecedingSum10Mil() {
        assertEquals(
                new BigInteger(
                        ""),
                sequenceCalc.sumNthElement(TEN_MIL, 200));
    }

}
